#!/usr/bin/env node

import { McpServer } from '@modelcontextprotocol/sdk/server/mcp.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import { registerTools } from './tools/index.js';
import { logger } from './utils/logger.js';
import { config } from 'dotenv';

config();

async function main() {
	try {
		const server = new McpServer({
			name: '{{KEBAB_CASE_NAME}}',
			version: '1.0.0',
		});

		// Register all tools
		registerTools(server);

		const transport = new StdioServerTransport();
		await server.connect(transport);

		logger.info('üöÄ {{PROJECT_NAME}} MCP Server ready for Claude Desktop');
		logger.info('üìö Domain: {{DOMAIN}}');

		process.on('SIGINT', async () => {
			logger.info('Shutting down {{PROJECT_NAME}}...');
			process.exit(0);
		});
	} catch (error) {
		logger.error('ERROR: Failed to start server:', error);
		process.exit(1);
	}
}

// Handle uncaught errors
process.on('uncaughtException', (error) => {
	logger.error('FATAL: Uncaught exception:', error);
	process.exit(1);
});

process.on('unhandledRejection', (reason) => {
	logger.error('FATAL: Unhandled rejection:', reason);
	process.exit(1);
});

// Start the server
main().catch((error) => {
	logger.error('FATAL: Failed to start:', error);
	process.exit(1);
});

