const logLevel = process.env.LOG_LEVEL || 'info';

export const logger = {
	info: (...args: any[]) => console.error('[{{KEBAB_CASE_NAME}}:info]', ...args),
	error: (...args: any[]) => console.error('[{{KEBAB_CASE_NAME}}:error]', ...args),
	debug: (...args: any[]) => {
		if (logLevel === 'debug') {
			console.error('[{{KEBAB_CASE_NAME}}:debug]', ...args);
		}
	},
	warn: (...args: any[]) => console.error('[{{KEBAB_CASE_NAME}}:warn]', ...args)
};

