src/tools/hello-world.ts.content:
import { McpServer } from '@modelcontextprotocol/sdk/server/mcp.js';
import { z } from 'zod';
import { logger } from '../utils/logger.js';

export function registerHelloWorldTool(server: McpServer) {
	server.registerTool(
		'hello_world',
		{
			title: 'Hello World',
			description: 'Sample greeting tool',
			inputSchema: {
				name: z.string().optional().default('World').describe('Name to greet')
			}
		},
		async ({ name = 'World' }) => {
			logger.info(`Hello world tool called with name: ${name}`);

			try {
				return {
					content: [{
						type: 'text' as const,
						text: `Hello, ${name}! This is {{PROJECT_NAME}} working correctly.`
					}]
				};
			} catch (error) {
				logger.error('Error in hello_world tool:', error);
				return {
					content: [{
						type: 'text' as const,
						text: `Error: ${error instanceof Error ? error.message : String(error)}`
					}],
					isError: true
				};
			}
		}
	);
}

src/tools/get-info.ts.content:
import { McpServer } from '@modelcontextprotocol/sdk/server/mcp.js';
import { logger } from '../utils/logger.js';

export function registerGetInfoTool(server: McpServer) {
	server.registerTool(
		'get_info',
		{
			title: 'Get System Info',
			description: 'Get system information',
			inputSchema: {}
		},
		async () => {
			logger.info('Get info tool called');

			try {
				return {
					content: [{
						type: 'text' as const,
						text: `üìä System Information:\n\n- Project: {{PROJECT_NAME}}\n- Version: 1.0.0\n- Node.js: ${process.version}\n- Platform: ${process.platform}\n- Uptime: ${process.uptime()} seconds\n- Domain: {{DOMAIN}}`
					}]
				};
			} catch (error) {
				logger.error('Error in get_info tool:', error);
				return {
					content: [{
						type: 'text' as const,
						text: `Error: ${error instanceof Error ? error.message : String(error)}`
					}],
					isError: true
				};
			}
		}
	);
}

