#!/usr/bin/env node

import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import { CallToolRequestSchema, ListToolsRequestSchema } from '@modelcontextprotocol/sdk/types.js';
import { config } from 'dotenv';
import { startHttpServer } from './http-server.js';
import { MCP_TOOLS } from './mcp-tools.js';
import { ToolHandlers } from './tool-handlers.js';

config();

const log = (...args: any[]) => console.error('[{{KEBAB_CASE_NAME}}]', ...args);

async function main() {
	// Get transport mode from environment
	const mode = (process.env.MCP_TRANSPORT || 'stdio').toLowerCase();

	try {
		const toolHandlers = new ToolHandlers();

		if (mode === 'http') {
			// HTTP/SSE mode
			const port = parseInt(process.env.MCP_PORT || process.env.PORT || '3000');
			const corsOrigins = process.env.ALLOWED_ORIGINS || '*';
			const bearerToken = process.env.MCP_BEARER;
			const bodyLimit = process.env.MCP_BODY_LIMIT || '1mb';

			log(`Starting HTTP server on port ${port}`);
			await startHttpServer(toolHandlers, {
				port,
				corsOrigins,
				bearerToken,
				bodyLimit,
			});
		} else {
			// STDIO mode (default)
			const server = new Server(
				{ name: '{{KEBAB_CASE_NAME}}', version: '1.0.0' },
				{ capabilities: { tools: {} } }
			);

			server.setRequestHandler(ListToolsRequestSchema, async () => ({
				tools: MCP_TOOLS,
			}));

			server.setRequestHandler(CallToolRequestSchema, async (request) => {
				return await toolHandlers.handleToolCall(request);
			});

			const transport = new StdioServerTransport();
			await server.connect(transport);
			log('STDIO mode ready for Claude Desktop');
		}

		process.on('SIGINT', () => process.exit(0));
	} catch (error) {
		log('ERROR:', error);
		process.exit(1);
	}
}

main().catch((error) => {
	log('FATAL:', error);
	process.exit(1);
});

