import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { SSEServerTransport } from '@modelcontextprotocol/sdk/server/sse.js';
import { CallToolRequestSchema, ListToolsRequestSchema } from '@modelcontextprotocol/sdk/types.js';
import express from 'express';
import { MCP_TOOLS } from './mcp-tools.js';
import { ToolHandlers } from './tool-handlers.js';

interface HttpServerOptions {
	port?: number;
	corsOrigins?: string;
	bodyLimit?: string;
	bearerToken?: string;
}

export async function startHttpServer(
	toolHandlers: ToolHandlers,
	options: HttpServerOptions = {}
): Promise<void> {
	const {
		port = 3000,
		corsOrigins = '*',
		bodyLimit = '1mb',
		bearerToken,
	} = options;

	const app = express();

	// Middleware b√°sico
	app.use(express.json({ limit: bodyLimit }));

	// CORS
	app.use((req, res, next) => {
		res.header('Access-Control-Allow-Origin', corsOrigins);
		res.header('Access-Control-Allow-Methods', 'GET, POST, OPTIONS');
		res.header('Access-Control-Allow-Headers', 'Content-Type, Authorization');
		if (req.method === 'OPTIONS') {
			res.sendStatus(200);
			return;
		}
		next();
	});

	// Autenticaci√≥n Bearer (opcional)
	if (bearerToken) {
		app.use('/sse', (req, res, next) => {
			const auth = req.headers.authorization;
			if (!auth || !auth.startsWith('Bearer ') || auth.slice(7) !== bearerToken) {
				res.status(401).json({ error: 'Unauthorized' });
				return;
			}
			next();
		});
	}

	// Health check
	app.get('/health', (req, res) => {
		res.json({ status: 'healthy', service: '{{KEBAB_CASE_NAME}}' });
	});

	// Informaci√≥n del servidor
	app.get('/info', (req, res) => {
		res.json({
			name: '{{KEBAB_CASE_NAME}}',
			version: '1.0.0',
			description: '{{PROJECT_NAME}}',
			tools: MCP_TOOLS.length,
			transport: 'http-sse',
		});
	});

	// Endpoint SSE para MCP
	app.get('/sse', async (req, res) => {
		console.log('New SSE connection');

		const server = new Server(
			{ name: '{{KEBAB_CASE_NAME}}', version: '1.0.0' },
			{ capabilities: { tools: {} } }
		);

		server.setRequestHandler(ListToolsRequestSchema, async () => ({
			tools: MCP_TOOLS,
		}));

		server.setRequestHandler(CallToolRequestSchema, async (request) => {
			return await toolHandlers.handleToolCall(request);
		});

		const transport = new SSEServerTransport('/message', res);
		await server.connect(transport);
	});

	// Iniciar servidor
	app.listen(port, () => {
		console.log(`üöÄ HTTP Server running on port ${port}`);
		console.log(`üì° SSE endpoint: http://localhost:${port}/sse`);
		console.log(`üíö Health check: http://localhost:${port}/health`);
		if (bearerToken) {
			console.log(`üîê Authentication: Bearer token required`);
		}
	});
}

