import { Tool } from '@modelcontextprotocol/sdk/types.js';

export const MCP_TOOLS: Tool[] = [
	{
		name: 'hello_world',
		description: 'Herramienta de ejemplo que saluda',
		inputSchema: {
			type: 'object',
			properties: {
				name: {
					type: 'string',
					description: 'Nombre para saludar',
					default: 'World',
				},
			},
		},
	},
	{
		name: 'get_server_info',
		description: 'Obtiene informaci√≥n del servidor MCP',
		inputSchema: {
			type: 'object',
			properties: {},
		},
	},
];

src/tool-handlers.ts.content:
import { ErrorCode, McpError } from '@modelcontextprotocol/sdk/types.js';

export class ToolHandlers {
	async handleToolCall(request: any) {
		const { name, arguments: args } = request.params;

		try {
			switch (name) {
				case 'hello_world':
					return await this.handleHelloWorld(args);
				case 'get_server_info':
					return await this.handleGetServerInfo(args);
				default:
					throw new McpError(ErrorCode.MethodNotFound, `Herramienta desconocida: ${name}`);
			}
		} catch (error) {
			console.error(`Error in tool ${name}:`, error);
			throw new McpError(
				ErrorCode.InternalError,
				`Error ejecutando herramienta '${name}': ${error instanceof Error ? error.message : String(error)}`
			);
		}
	}

	private async handleHelloWorld(args: any) {
		const name = args?.name || 'World';
		return {
			content: [{
				type: 'text',
				text: `¬°Hola, ${name}! Este es {{PROJECT_NAME}} funcionando correctamente v√≠a ${process.env.MCP_TRANSPORT || 'STDIO'}.`,
			}],
		};
	}

	private async handleGetServerInfo(args: any) {
		const mode = process.env.MCP_TRANSPORT || 'stdio';
		const port = process.env.MCP_PORT || process.env.PORT;

		return {
			content: [{
				type: 'text',
				text: `üìä Informaci√≥n del servidor:\n\n- Proyecto: {{PROJECT_NAME}}\n- Versi√≥n: 1.0.0\n- Modo: ${mode.toUpperCase()}\n${port ? `- Puerto: ${port}\n` : ''}- Node.js: ${process.version}\n- Plataforma: ${process.platform}\n- Tiempo activo: ${process.uptime()} segundos\n- Capacidades: HTTP/SSE + STDIO`,
			}],
		};
	}
}
